INSERT INTO `Events` (`Event_ID`, `Title`, `Date`, `Description`, `Address`, `City`, `Province`, `Winery`, `Image`) VALUES
(7101, 'Stellenbosch Wine Festival', '2023-06-02', 'The Stellenbosch Wine Festival is a celebration of the finest wines from the Stellenbosch region. Join us for a weekend of wine tastings, cellar tours, food pairings, and live music.', 'Stellenbosch, Western Cape', 'Stellenbosch', 'Western Cape', 8157, 'image_stellenbosch.jpg'),
(7102, 'Franschhoek Bastille Festival', '2023-07-15', 'Experience a taste of France in the heart of Franschhoek. The Bastille Festival brings together the best of South African and French wines, along with gourmet food stalls, live entertainment, and a festive atmosphere.', 'Franschhoek, Western Cape', 'Franschhoek', 'Western Cape', 8223, 'image_franschhoek.jpg'),
(7103, 'Cape Winemakers Guild Auction Showcase', '2023-09-09', 'The Cape Winemakers Guild Auction Showcase is an exclusive event featuring limited-edition wines from the esteemed Cape Winemakers Guild. Don\'t miss this opportunity to taste and purchase exceptional wines.', 'Cape Town, Western Cape', 'Cape Town', 'Western Cape', 8345, 'image_cape_town.jpg'),
(7104, 'Hermanus Wine and Food Festival', '2023-10-21', 'Indulge in the flavors of the Hemel-en-Aarde Valley at the Hermanus Wine and Food Festival. Sample a variety of wines, savor gourmet food, and enjoy live music and breathtaking views of the coast.', 'Hermanus, Western Cape', 'Hermanus', 'Western Cape', 8412, 'image_hermanus.jpg'),
(7105, 'Robertson Slow Food & Wine Festival', '2024-04-07', 'Experience the slow food and wine movement at the Robertson Slow Food & Wine Festival. Discover the region\'s sustainable farming practices, taste local wines and farm-to-table cuisine, and participate in workshops and activities.', 'Robertson, Western Cape', 'Robertson', 'Western Cape', 8569, 'image_robertson.jpg'),
(7108, 'Paarl Harvest Celebration', '2024-03-10', 'Join us in Paarl for the annual Harvest Celebration. Participate in grape picking, traditional winemaking activities, taste local wines, and enjoy live music and entertainment in this historic wine region.', 'Paarl, Western Cape', 'Paarl', 'Western Cape', 8821, 'image_paarl.jpg'),
(7110, 'McGregor Food & Wine Festival', '2024-04-14', 'Discover the wines of the Robertson Wine Valley at the McGregor Food & Wine Festival. Sample a variety of wines, indulge in gourmet food, enjoy live music, and explore the picturesque village of McGregor.', 'McGregor, Western Cape', 'McGregor', 'Western Cape', 9015, 'image_mcgregor.jpg'),
(7321, 'The Vaal Wine & Spirit Expo', '2023-06-10', 'TIME:    12h00\r\nCOST:    R300\r\nVENUE:    Stonehaven on Vaal, Next to Old Baddrift Bridge, Vaal River, Vanderbijlpark\r\nGAUTENG\r\nWhy travel all the way to the Cape when you can enjoy unlimited superb wine tasting right on the banks of the Vaal River in Gauteng! This year, The 19th Annual Vaal Wine Expo is hosted at Stonehaven on Vaal on Saturday, 03 June.\r\nSwirl, sniff and sip great SA wines while cruising down Millionaires Mile on a luxury cruiser in Gauteng! Once again, the Vaal River Meander Wine & Spirit Expo is being hosted jointly by Stonehaven on Vaal on the banks of the Vaal River in Gauteng. \r\n\r\nThere is a pleasant walkway along the river bank between the two venues where you can enjoy meandering freely between all the different wine tasting stalls from one venue to the other, with only the ducks on the river to bother you!\r\n\r\nTickets cost R300 per person which includes:\r\n\r\nA branded tasting glass to use and take home.\r\nUnlimited tastings of over 200 different wines.\r\nA 30 minute Vaal River Cruise voucher OR a R50.00 food voucher to use on the day\r\nAccess to food stalls, kiddies play area, restaurant & bar.\r\nPLEASE NOTE:\r\n\r\nTickets limited to 2000 tickets & are set to sell out fast!\r\nNO tickets available on the day.\r\nKids & designated drivers allowed free entry\r\nFor more info email info@vaalwineroute.co.za. ', 'Stonehaven on Vaal, Rosebush Lane, Zuurfontein, 1911', 'Vanderbijlpark', 'Gauteng', 8346, 'https://images.wine.co.za/GetImage.ashx?ImageType=diary&width=500&mode=max&IMAGEID=279369'),
(7520, 'European Food and Wine Evening at Diemersdal', '2023-06-09', 'TIME:    18h00\r\nCOST:    R625 p/p\r\nVENUE:    The Farm Eatery at Diemersdal\r\nDURBANVILLE\r\nJoin us on 05 June 2023 for a European Food and Wine evening at Diemersdal.\r\nJoin us at Diemersdal\'s Farm Eatery for an exquisite evening of culinary delights and fine wine as we present a 3-course dinner paired with a selection of exceptional Sauvignon Blanc wines from Italy, Austria, France, and Diemersdal. Prepare your taste buds for a journey through the diverse terroirs and distinct flavors of these renowned wine regions.\r\n\r\nTo secure your place at this remarkable event, we kindly request a booking deposit of R200 per person. Please note that the deposit is payable upfront to confirm your reservation. Additionally, a 12% service fee will be added to the final bill.\r\n\r\nWe aim to cater to all dietary requirements to ensure an enjoyable experience for all guests. Kindly communicate any specific dietary needs or restrictions in advance, and our culinary team will be delighted to accommodate you.\r\n\r\nJoin us for an unforgettable evening of exceptional cuisine, impeccable wines, and delightful company. Reserve your spot now and embark on a remarkable journey through the world of Sauvignon Blanc.\r\n\r\nWe look forward to hosting you at this remarkable event!\r\n\r\nClick HERE to book your table or email info@thefarmeatery.co.za or call 021 976 1810 or 081 399 616', 'The Farm Eatery @ Diemersdal, Diemersdal Wine Estate, M58 Koeberg Rd, Durbanville,7550', 'Cape Town', 'Western Cape', 8243, 'https://images.wine.co.za/GetImage.ashx?ImageType=diary&width=500&mode=max&IMAGEID=279504'),
(7645, 'Cellar Rats Winter Wine Festival', '2023-06-15', 'TIME:    12h00\r\nCOST:    R240 p/p\r\nVENUE:    Riversands Farm Village, Rose Road, Riversands\r\nGAUTENG\r\nEnjoy an afternoon of wine tasting along the riverbank at Riversands Farm Village with your friends and family.\r\nA host of producers will be presenting a variety of wines, bubbles, craft gin, deli products and more for your enjoyment.\r\n\r\nThe Riversands Kids entertainment area is available (entrance is R60 per child and is bought at the entrance of the kids area).\r\n\r\nWhat does your ticket include:\r\n\r\nEntrance into the wine festival area.\r\nWine tasting glass.\r\nUnlimited tastings - NO additional tasting charges.\r\nIf you like a wine/s you can buy it right there and take it home with you.\r\nNo cooler boxes, picnic baskets or braai\'s will be permitted as food, soft drinks, water and alcoholic beverages will be on sale through the Riversands stalls.\r\n\r\nWe cannot wait to see you there, bring along your camping chairs, blankets, umbrellas or gazebos for shade. Set up a spot along the riverbank and enjoy the food and wine that will be available.\r\n\r\nOur event is a dog friendly event, as long as your pet stays on the leash.', 'Riversands Farm Village, Riversands Commercial Park, Rose Road, Knopjeslaagte, Midrand, 2189', 'Pretoria', 'Gauteng', 8243, 'https://images.wine.co.za/GetImage.ashx?ImageType=diary&width=500&mode=max&IMAGEID=279361');
