(49, 'Torres Mas La Plana', 8241, 'Torres Mas La Plana is an iconic Cabernet Sauvignon from the Penedès region. It exhibits intense black fruit, cassis, and tobacco flavors with a velvety texture.', 2015, 'Penedès', ' R60.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 9, 'Cabernet Sauvignon', 'Red', 'Grilled beef tenderloin'),
(50, 'Torres Salmos', 8241, 'Torres Salmos is a powerful and complex red blend with flavors of black cherry, blackberry, and spices. It has a full-bodied palate and a long, elegant finish.', 2017, 'Priorat', ' R45.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 9, 'Red Blend', 'Red', 'Lamb tagine'),
(51, 'Torres Celeste Crianza', 8241, 'Torres Celeste Crianza is a smooth and fruity red wine with notes of ripe berries, vanilla, and spices. It offers a well-balanced palate and a lingering finish.', 2018, 'Ribera del Duero', ' R20.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 8, 'Tempranillo', 'Red', 'Grilled pork ribs'),
(52, 'Torres Floralis Moscatel Oro', 8241, 'Torres Floralis Moscatel Oro is a sweet and aromatic dessert wine with intense flavors of orange blossom, honey, and dried fruits. It is perfect with creamy desserts.', 2019, 'Catalonia', ' R15.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 8, 'Moscatel', 'Desert', 'Crème brûlée'),
(53, 'Torres Altos Ibéricos Crianza', 8241, 'Torres Altos Ibéricos Crianza is a smooth and fruity red wine with flavors of black cherry, plum, and hints of vanilla. It has a medium-bodied palate and a soft finish.', 2017, 'Rioja', ' R18.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 7, 'Tempranillo', 'Red', 'Grilled lamb'),
(54, 'Torres Natureo Syrah', 8241, 'Torres Natureo Syrah is a non-alcoholic red wine with ripe berry aromas and flavors of blackberry, blackcurrant, and spices. It is a great option for non-drinkers.', 2019, 'Catalonia', ' R10.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 7, 'Syrah', 'Non-alcoholic', 'Grilled vegetables'),
(55, 'Torres Viña Esmeralda', 8241, 'Torres Viña Esmeralda is a fresh and aromatic white wine with floral and citrus notes. It has a crisp acidity and a clean, refreshing finish.', 2020, 'Penedès', ' R12.00 ', 'https://www.torres.es/sites/default/files/2022-04/VardonKennett_725x480px.jpg', 7, 'Muscat', 'White', 'Grilled seafood'),
